query:
query InvestorProfileLoad($personId: ID!, $firstFundingRounds: Int, $firstInvestmentsOnRecord: Int, $firstNetworkListInvestorProfiles: Int, $firstInvestingConnections: Int) {
  investor_profile(person_id: $personId) {
    direct_introduction {
      id
      __typename
    }
    ...investorProfileFields
    __typename
  }
}

fragment investorProfileFields on InvestorProfile {
  id
  claimed
  can_edit
  has_profile_vote
  include_in_list
  in_founder_investor_list
  in_diverse_investor_list
  in_female_investor_list
  in_invests_in_diverse_founders_investor_list
  in_invests_in_female_founders_investor_list
  leads_rounds
  image_urls_edit_mode
  is_preferred_coinvestor
  person {
    id
    slug
    first_name
    last_name
    name
    linkedin_url
    facebook_url
    twitter_url
    crunchbase_url
    angellist_url
    roles
    url
    is_me
    is_on_target_list
    first_degree_count
    relationship_strength
    email_from_my_contacts_list
    __typename
  }
  stages {
    id
    kind
    display_name
    __typename
  }
  investment_locations {
    id
    kind
    display_name
    __typename
  }
  areas_of_interest {
    id
    __typename
  }
  position
  min_investment
  max_investment
  target_investment
  image_urls
  areas_of_interest_freeform
  no_current_interest_freeform
  vote_count
  headline
  previous_position
  previous_firm
  location {
    id
    display_name
    __typename
  }
  firm {
    id
    current_fund_size
    name
    slug
    __typename
  }
  degrees {
    id
    name
    field_of_study
    school {
      id
      name
      display_name
      total_student_count
      __typename
    }
    __typename
  }
  positions {
    id
    title
    company {
      id
      name
      display_name
      total_employee_count
      __typename
    }
    start_date {
      month
      year
      __typename
    }
    end_date {
      month
      year
      __typename
    }
    __typename
  }
  media_links {
    id
    url
    title
    image_url
    __typename
  }
  investor_lists {
    id
    slug
    stage_name
    vertical {
      id
      kind
      display_name
      __typename
    }
    location {
      id
      __typename
    }
    __typename
  }
  investments_on_record(first: $firstInvestmentsOnRecord) {
    pageInfo {
      hasNextPage
      __typename
    }
    record_count
    edges {
      node {
        id
        company_display_name
        total_raised
        coinvestor_names
        investor_profile_funding_rounds {
          id
          is_lead
          board_role {
            id
            title
            __typename
          }
          funding_round {
            id
            stage
            date
            amount
            __typename
          }
          __typename
        }
        __typename
      }
      __typename
    }
    __typename
  }
  investor_profile_funding_rounds(first: $firstFundingRounds) {
    pageInfo {
      hasNextPage
      __typename
    }
    record_count
    edges {
      node {
        id
        is_lead
        board_role {
          id
          title
          __typename
        }
        funding_round {
          id
          amount
          date
          stage
          company {
            id
            display_name
            __typename
          }
          __typename
        }
        __typename
      }
      __typename
    }
    __typename
  }
  network_list_investor_profiles(first: $firstNetworkListInvestorProfiles) {
    list_type
    edges {
      node {
        id
        image_urls
        position
        person {
          id
          name
          first_name
          last_name
          slug
          __typename
        }
        firm {
          id
          name
          slug
          __typename
        }
        __typename
      }
      __typename
    }
    __typename
  }
  network_list_scouts_and_angels_profiles(first: $firstNetworkListInvestorProfiles) {
    list_type
    edges {
      node {
        id
        image_urls
        position
        person {
          id
          name
          first_name
          last_name
          slug
          __typename
        }
        firm {
          id
          name
          slug
          __typename
        }
        __typename
      }
      __typename
    }
    __typename
  }
  investing_connections(first: $firstInvestingConnections) {
    record_count
    edges {
      node {
        id
        target_person {
          id
          name
          slug
          first_name
          last_name
          investor_profile {
            id
            image_urls
            firm {
              id
              name
              slug
              __typename
            }
            __typename
          }
          __typename
        }
        __typename
      }
      __typename
    }
    __typename
  }
  __typename
}
